        </div>
        <div>
            <div>{() => `double map runs: ${doubleMapRuns()}`}</div>
            {double.map((item) => <div>{item}</div>)}
        </div>
        <div>
            <div>{() => `incremental double map runs: ${incrementalDoubleMapRuns}`}</div>
            {incrementalDouble.map((item) => <div>{item}</div>)}
        </div>
    </div>
}

